import React from 'react';
import './App.css';
import AddTask from './addTask'

export default class App extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            allTask: []
        };
        this.addTask= this.addTask.bind(this);
        this.delTask= this.delTask.bind(this);
        this.completedTask = this.completedTask.bind(this);
    }
    addTask(todo){
        this.setState({allTask: [todo, ...this.state.allTask]});
    }


    delTask(index){
        this.setState({allTask: this.state.allTask.filter((a,ind) => ind !==index)});
    }

    completedTask(index){
        this.setState({
            allTask: this.state.allTask.filter((item,i)=>{
                if(index === i){
                    item.isCompleted = !item.isCompleted;
                    return item;
                }
                else{
                    return item;
                }
            })
        })

    }


    render(){
        // console.log(this.props.tasks, this.state.allTask, "props and state")
        const listItems = this.state.allTask.map((task,index) =>
        <div className= "tasktab" keys = {task.id}>

            <label>
            <input type = "checkBox" checked = {this.state.allTask[index].isCompleted} onClick = {()=> {
            this.completedTask(index);
            }} />

            <p className={(this.state.allTask[index].isCompleted)? "Completed": "Pending"}>
                {task.text}
            </p>

            <button class ="Destroy" type="button" onClick = {()=>{ this.delTask(index);}}> X </button>
            </label>
        </div>
        );

        return(
            <div>
            <AddTask onSubmit={this.addTask} />
           <ul> {listItems}</ul>
            </div>
        );
    };
}



