import React from "react";
import "./App.css";

export default class EditTask extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      editable: false,
    };
    this.onDoubleClick = this.onDoubleClick.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

//   handleChange(event) {
//     this.setState({ [event.target.name]: event.target.value });
//     this.props.handleChange(event);
//   }

  handleSubmit(event) {
    event.preventDefault();
    console.log(this.props.currTask.name);
    this.props.onSubmit({
      id: this.props.currTask.id,
      text: this.props.currTask.name,
      isCompleted: this.props.currTask.isCompleted
    });
  }

  onDoubleClick() {
    this.setState({ editable: true });
  }

//   componentWillReceiveProps(nextProps){
//       this.setState({
//         ctask: nextProps.currTask,
//         newTask: nextProps.currTask.text
//       })
//   }
  render() {
    if (this.state.editable === false) {
      return (
        <label
          className={this.props.currTask.isCompleted ? "Completed" : "Pending"}
          onDoubleClick={this.onDoubleClick}
        >
          {" "}
          {this.props.currTask.name}{" "}
        </label>
      );
    } else {
      return (
        <div>
          <form onSubmit={this.handleSubmit}>
            <input
              className='textplace'
              type='text'
              value={this.props.currTask.name}
              onChange={this.props.handleChange}
            />
          </form>
        </div>
      );
    }
  }
}
