import React from 'react';

export default class ShowTask  extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            alltask: this.props.tasks
        };
        this.delTask= this.delTask.bind(this);
    }
    delTask(index){
        this.state.alltask.splice(index,1);
        this.setState({alltask: this.state.alltask});
    }


    render(){
        <div>
    const listItems = this.props.tasks.map((task,index) =>

        <p>{task}</p> 
        <button onClick = {this.delTask(index)} >Delete</button>

        );
        </div>
        return(
            <ul> {listItems }</ul>
        );
    };
}
