import React from 'react';
import './App.css';
import App from './App';
// import DeleteTask from './deleteTask';

export default class ShowTask  extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            allTask: []
        };
        his.addTask= this.addTask.bind(this);
        this.delTask= this.delTask.bind(this);
        this.completedTask = this.completedTask.bind(this);
    }
    addTask(todo){
        this.setState({allTask: [todo, ...this.state.allTask]});
    }


    delTask(index){
        this.setState({allTask: this.state.allTask.filter((a,ind) => ind !==index)});
    }

    completedTask(index){
        // (this.state.getDone[index]==="Pending") ? 
        // (this.state.getDone[index]="Done"): (this.state.getDone[index]="Pending") ;
        // this.setState({getDone: this.state.getDone});

        this.setState({
            allTask: this.state.allTask.filter((item,i)=>{
                if(index === i){
                    item.isCompleted = !item.isCompleted;
                    return item;
                }
                else{
                    return item;
                }
            })
        })

    }


    render(){
        return(
            <div>
            <App onsubmit={this.addTask} />
            <ul> {listItems}</ul>
            </div>
        );
    };
}



