import React from 'react';
// import DeleteTask from './deleteTask';

export default class ShowTask  extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            alltask: this.props.tasks,
            done: []
        };
        this.delTask= this.delTask.bind(this);
    }
    delTask(index){
        console.log("hello friends" + index);
        // this.state.alltask.splice(index,1);
        // this.setState({alltask: this.state.alltask});
    }



    render(){
        var todoClass = this.state.done ? "done" : "undone";
    const listItems = this.props.tasks.map((task,index) =>
        <div>
        {/* <p keys = {index} onDoubleClick ={this.contentEditable=true}>{task} */}
        if {this.state.done[index]=== undefined} ({this.state.done[index]=false});

         <div className={todoClass} >{task}
        </div>
        {/* pending task */}
        <input type="button" value= "Pending" onClick = {()=> {this.setState({done: true});}} />


       {/* to delete task  */}
        <input type="button" value= "Delete" onClick = {()=>{
            this.state.alltask.splice(index,1);
            this.setState({alltask: this.state.alltask});
        } } />
        </div>
        );
        return(
            <ul> {listItems}</ul>
        );
    };
}
