import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
// import App from './App';
import * as serviceWorker from './serviceWorker';

class App extends React.Component {
   constructor (props){
      super(props);
      this.state = {newTask: "", submitTask: ""};
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
  }

    handleChange(event){
      this.setState({newTask: event.target.newTask});
    }

    handleSubmit(event){
      console.log(this.state.newTask); 
        this.setState({submitTask: this.state.newTask});
        // event.preventDefault(); 


    }

  render(){
  return (
    <div className="App">
    <form onSubmit = {this.handleSubmit}>
      <label>
        Task:
        <input type="text" value = {this.state.newTask} onChange = {this.handleChange} />
        </label>
        <input type = "submit" value = "Submit" />
    </form>
    <p> {this.state.submitTask}</p>
    </div>


  );
  }
}

ReactDOM.render(<App />, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
