import React from 'react'
import './App.css';
import AddTask from './addTask'
import EditTask from './editTask'

export default class App extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            allTask: [],
            todoToShow: "All"
        };
        this.addTask= this.addTask.bind(this);
        this.delTask= this.delTask.bind(this);
        this.selectAll= this.selectAll.bind(this);
        this.completedTask = this.completedTask.bind(this);
    }
    addTask(todo){
        console.log(todo, "todo")
        if(todo.text!==''){
            this.setState({allTask: [todo, ...this.state.allTask]});
        }
    }

    updateTask(todo){
        this.setState({allTask: this.state.allTask.filter(item=> {
            if(item.id===todo.id){
                item.text = todo.text;
                return item;
            }
            else {return item;}
        })
    })
}


    delTask(index){
        this.setState({allTask: this.state.allTask.filter(a => a.id !==index)})
    }

    completedTask(index){
        this.setState({
            allTask: this.state.allTask.filter(item=>{
                if(index === item.id){
                    item.isCompleted = !item.isCompleted;
                    return item;
                }
                else{
                    return item;
                }
            })
        })
    }

    updateTodoToShow(s){
        this.setState({todoToShow: s})
    }

    selectAll(){

        let s = this.state.allTask.some(function (val) { 
            return val.isCompleted===false;
          });
        if(s===true){
        this.setState({
            allTask: this.state.allTask.filter(item=>{
                    item.isCompleted=true;
                    return item;
                })  
            })
        }

        else{
            this.setState({
                allTask:this.state.allTask.filter(item=>{
                    item.isCompleted=false;
                    return item;
                })
            })
        }
    }

    render(){
        // console.log(this.props.tasks, this.state.allTask, "props and state")
        let todos =[];
        if (this.state.todoToShow==="All") {
            todos = this.state.allTask;
        } else if(this.state.todoToShow==="Active") {
            todos = this.state.allTask.filter(task=> !task.isCompleted);
        } else if(this.state.todoToShow==="Completed") {
            todos = this.state.allTask.filter(task=> task.isCompleted);
        }

        return(
            <div>
            <h1 className= "heading">Todos</h1>
            <div className= "tasktab">
            <div className="tasktab2">
            <button className="toggle" type="button" onClick = {this.selectAll}> ✓ </button>
            <AddTask onSubmit={this.addTask} />
            </div>
               {todos.map((task) =>
                <div className= "tasktab2">            
                <input type = "checkBox" checked = {task.isCompleted} onClick = {()=> {
                    this.completedTask(task.id);
                }} />

                <EditTask currTask = {task} onSubmit = {this.updateTask} />
                <button className ="Destroy" type="button" onClick = {()=>{ this.delTask(task.id);}}> X </button>
                </div>
                )
            }
            <label>  {todos.filter(task=> !task.isCompleted).length} items left </label>
            <button onClick={()=> this.updateTodoToShow("All")}>All </button>
            <button onClick={()=>this.updateTodoToShow("Active")}>Active </button>
            <button onClick={()=> this.updateTodoToShow("Completed")}>Completed </button>
            </div>
            </div>
        );
    };
}

