import React from 'react';
// import ReactDOM from "react-dom"
// import logo from './logo.svg';
import './App.css';
// import DeleteTask from './deleteTask'
import ShowTask from './showTask'

export default class App  extends React.Component {
   constructor (props){
      super(props);
      this.state = {
        newTask: ''
      };
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
  }
    allTask =[];

    handleChange(event){

      // alert(this.state.newTask);
      this.setState({newTask: event.target.value});
    }

    handleSubmit(event){
      // alert(this.state.newTask); 
      // this.setState({allTasks: this.state.allTasks.push(this.state.newTask)});
      console.log([...this.allTask], "allTask")
      this.allTask.push({task: this.state.newTask, isCompleted: false});
      // this.getDone.push("Pending");
      // this.state.allTasks.push(this.state.newTask);
      this.setState({newTask: ''});
      event.preventDefault(); 
    }

  render(){

      return (
          <div className="App">
          <h1>ToDo List</h1>
          <form onSubmit = {this.handleSubmit}>
            <label>
              Task:              
              <input type="text" value = {this.state.newTask} onChange = {this.handleChange} />
              </label>
              <input type = "submit" value = "Submit" />
          </form>

          {/* <ShowTask tasks = {this.allTask} done = {this.getDone} /> */}
          <ShowTask tasks = {this.allTask} />
          {/* <ul>{listItems}</ul> */}
          </div> 


  );
  }
}
