import React from 'react'
import './App.css'

export default class EditTask extends React.Component {
constructor(props){
    super(props);
    this.state = {
        task: this.props.currTask,
        editable: false,
        newTask: this.props.currTask.text
    }
    this.onDoubleClick = this.onDoubleClick.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

}

handleChange(event){
    this.setState({[event.target.name]: event.target.value});
  }

  handleSubmit(event){
    event.preventDefault();
    console.log(this.state.newTask);
      this.props.onSubmit({
          id: this.state.task.id,
          text: this.state.newTask,
          isCompleted: this.state.task.isCompleted
      })
  }


onDoubleClick() {
    this.setState({editable: true});
}

render(){
    if(this.state.editable===false){
    return(
        <label className={(this.state.task.isCompleted)? "Completed": "Pending"} onDoubleClick = {this.onDoubleClick}> {this.state.task.text} </label>
    );
    }
    else{
        return(
        <div>
        <form onSubmit = {this.handleSubmit}>         
            <input className= "textplace" type= "text" name= "newTask" value = {this.state.newTask} onChange = {this.handleChange} />
        </form>
        </div> 
        );
    }
    }

}
