import React from 'react';
// import ReactDOM from "react-dom"
// import logo from './logo.svg';
import './App.css';

export default class App extends React.Component {
   constructor (props){
      super(props);
      this.state = {newTask: "", submitTask: "", allTasks: []};
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
  }

    handleChange(event){
      this.setState({newTask: event.target.newTask});
    }

    handleSubmit(event){
      alert("5"+ this.state.newTask); 
      this.setState({submitTask: this.state.newTask});
      return <p> 5</p>
        // event.preventDefault(); 


    }

  render(){
  return (
    <div className="App">
    <form onSubmit = {this.handleSubmit}>
      <label>
        Task:
        <input type="text" value = {this.state.newTask} onChange = {this.handleChange} />
        </label>
        <input type = "submit" value = "Submit" onClick = {this.handleSubmit}/>
    </form>
    <p> {this.state.submitTask}</p>
    </div>


  );
  }
}
